package com.robbyp.coffeecake//import org.joda.time.*//import org.joda.time.contrib.hibernate.*class Transaction {        BigDecimal quantity = 0    BigDecimal price = 0//    LocalDate date    //String investment    String type    String description    //static belongsTo = [account:Account]        BigDecimal getTotal() {        quantity * price    }        static transients = ['total']    //    static mapping = {//        date type: PersistentLocalDate//    }        static constraints = {        //investment(blank: false)        type(blank: false)    }        String toString() {        "Transaction: [" + description + "], for " + price //+ " on [" + date + "], of [" + type + "]."    }}